      setReverseTranslation({
        englishText: "Translation failed - please try again",
        sourceLang: "Error"
      });
    } finally {
      setIsTranslating(false);
    }
  };

  // Manual translation function (for button click)
  const translateToEnglish = async () => {
    if (!studentText.trim()) {
