              <div className={`w-3 h-3 rounded-full ${connectionStatus ? 'bg-green-500' : 'bg-red-500'}`} />
              <span className="text-sm font-medium text-gray-600">{connectionStatus ? 'Connected' : 'Disconnected'}</span>
            </div>
          </div>
        </div>

        <div className="flex gap-4 mb-4">
          <Link href="/" className="text-emerald-600 font-medium">Full Translator</Link>
          <Link href="/streamlined" className="text-gray-500 hover:text-emerald-600">Batch Mode</Link>
          <Link href="/speech" className="text-gray-500 hover:text-emerald-600">Speech Interface</Link>
        </div>
      </header>
        {/* Global Error */}
        {globalError && (
          <ErrorPanel 
            message={globalError} 
            onDismiss={dismissError}
            className="mb-6"
          />
        )}

        <div className="grid grid-cols-1 xl:grid-cols-2 gap-8">
          {/* English to Languages */}
          <div className="space-y-6">
            <SectionCard 
              title="English â†’ Languages" 
              icon={<Languages />}
              aside={
                <Badge variant="outline" className="text-emerald-700 border-emerald-300">
                  {selectedLanguages.length}/12 selected
                </Badge>
              }
            >
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-3">
                    Select Target Languages (up to 12)
                  </label>
                  <LanguageSelector
                    selectedLanguages={selectedLanguages}
                    onChange={handleLanguageChange}
                  />
                </div>

                <div>
                  <label className="block text-lg font-semibold text-gray-800 mb-3">
                    English Text to Translate
                  </label>
                  <div className="relative">
                    <LargeTextarea
                      value={englishText}
                      onChange={(e) => setEnglishText(e.target.value)}
                      placeholder="Type your English message hereâ€¦"
                      data-testid="input-english-text"
                      className="pr-16"
                    />
                    <Button
                      onClick={startEnglishRecording}
                      disabled={isRecordingEnglish}
                      className="absolute bottom-4 right-4 w-10 h-10 rounded-full p-0 bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 focus:ring-2 focus:ring-blue-300"
                      data-testid="button-record-english"
                    >
                      <Mic className="w-5 h-5" />
                    </Button>
                  </div>
                  {isRecordingEnglish && (
                    <div className="mt-2 text-sm text-blue-600 flex items-center">
                      <div className="w-2 h-2 bg-red-500 rounded-full animate-pulse mr-2"></div>
                      Recording English speech...
                    </div>
